package Aldagaia_Ariketak;

public class Ariketa_05 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Valor del "Radiodelcirculo"
		float Radiodelcirculo = 3f;
		//Valor del "PI"
		float PI = 3.1416f;
		//Valor "perimetro igual a 2 por PI por Radiodelcirculo"
		float perimetro = 2 * PI * Radiodelcirculo;
		//Mostrar en pantalla
		System.out.println("Este es el perimetro: " + perimetro);
	}

}
